import React, {useEffect, useState} from "react";
import firestore from "../config/fbconfig";
import { useHistory } from 'react-router-dom';
import socket from '../socket';

const RoomList = () => {

        const [roomList, setRoomList] = useState([]);

        useEffect(() => {
          async function fetchData(){
            await fetch('http://localhost:9000/room-list')
              .then(function(response) {
                return response.json();
            }).then((json) => {
              console.log(json);
              setRoomList(json);
              json.forEach((room) => {
                console.log(room);
                socket.emit('BE-get-all-users', room);
              })
            })}
          fetchData();
        }, []);

        const history = useHistory();

// Room list from server
        // fetch('http://localhost:9000/room-list').then(r =>
        //     r.json()
        // ).then(roomList => {
        //     setRoomList(roomList);
        // })

        return <div style={{display: 'flex', alignItems: 'start', flexDirection: 'column'}}>
            <h1 style={{margin: 20}}>Rooms</h1>
            <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'start'}}>
                {
                    (roomList.length > 0
                        ? roomList.map((room) =>
                            <div
                                onClick={() => {
                                    history.push('/join?roomId=' + room.roomId);
                                }}
                                style={{backgroundColor: '#222', width: 300, padding: 10, margin: 20, borderRadius: 20}}
                            >
                                {room.roomName}
                            </div>)
                        : <div>Loading</div>)
                }
            </div>
        </div>
    }
;


export default RoomList;
